import react from "react";

export const Cards = ({ title, image, ingredients }) => {
  return (
    <>
      <div className="container ">
        <div class="card-columns float-left">
          <div className="card  " style={{ width: "18rem" }}>
            <img
              className="img-fluid w-50 mx-auto rounded-circle"
              src={image}
              alt="img"
            />
            <div className="card-body">
              <h5 className="card-title small">{title}</h5>
              <p className="card-text ml-1">{ingredients}</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
